
public class Villager {

	private String name;
	private String horse;
	private String whisky;
	
	public Villager() {
		// TODO Auto-generated constructor stub
	}
	public String getName() {
		return name;
	}
	public String getWhisky()	{
		return whisky;
	}
	public Villager(String name, String horse, String whisky) {
		super();
		this.name = name;
		this.horse = horse;
		this.whisky = whisky;
	}

}
